const initialState = {
    gameInfo: { id: "mockGame1", name: "Adventure Mock" },
    playerInfo: { id: "p1", name: "Hero", hp: 100 },
    monsterInfo: { id: "m1", name: "Monster", hp: 50 },
    playerCards: [
      { id: "c1", effect: "damage", value: 10 },
      { id: "c2", effect: "damage", value: 20 },
      { id: "c3", effect: "heal", value: 15 },
    ],
    newGame: { game: null },
  };

  export default function gameReducer(state = initialState, action) {
    switch (action.type) {
      case "NEXT_TURN":
        return {
          ...state,
          monsterInfo: action.payload.monsterInfo,
          playerCards: action.payload.playerCards,
        };
      default:
        return state;
    }
  }
